import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import { Container, Row, Col, Tabs, Tab, CardDeck, Card, ListGroup } from 'react-bootstrap';

const CardArrival = (props) => {
  return (
    <Card bg="info" text="black" lg={4} >
    {Object.keys(props.data).map((keyName, i) => (


    <Card.Body>
      <Card.Title>Route {i+1}</Card.Title>
      <Card.Text>
        <ListGroup>
          <ListGroup.Item >{props.data[keyName][0]} minutes</ListGroup.Item>
          <ListGroup.Item>{props.data[keyName][1]} minutes</ListGroup.Item>
        </ListGroup>
      </Card.Text>
    </Card.Body>

))}
  </Card>

  )
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      response: false,
      stopId:1,
      Stop1:{"R1":'',"R2":'',"R3":''},
      Stop2:{"R1":'',"R2":'',"R3":''},
      endpoint: "http://localhost:8000"
    };
    this.handleSelect = this.handleSelect.bind(this);
  }
  componentDidMount() {

    this.connectSocket(1);
  }

  handleSelect(key) {
    console.log(key);
    this.setState({stopId:key}, () => {
      this.connectSocket();
   });

   }

  connectSocket()
  { 

    const { endpoint } = this.state;
    let stop= "Stop"+this.state.stopId;
    const socket = socketIOClient(endpoint);

    console.log(stop)
    socket.emit(stop,"");
    socket.on(stop, data => this.setState({ [stop]: JSON.parse(data) }));
  }
  render() {
    const { Stop1,Stop2 } = this.state;


    return (

      <Container>

        {Stop1 ?

          <Row>


          <Col xs={12} md={12}>

              <Tabs activeKey={this.state.stopId} onSelect={this.handleSelect} id="uncontrolled-tab-example">
              <Col xs={6} md={6}>
                <Tab eventKey={1} title="Stop 1">
                  <CardDeck>
                  <CardArrival data={Stop1}/>

                  </CardDeck>
                </Tab>
                </Col>
                <Tab eventKey={2} title="Stop 2">
                  <CardDeck>
                  <CardArrival data={Stop2}/>
                  </CardDeck>
                </Tab>

              </Tabs>

            </Col>
          </Row> : <div>Loading...</div>}
      </Container>


    );
  }
}
export default App;
