import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import { Container, Row, Col, Tabs, Tab, CardDeck, Card, ListGroup } from 'react-bootstrap';


class App extends Component {
  constructor() {
    super();
    this.state = {
      response: false,
      Stop1:{"Route 1":'',"Route 2":'',"Route 3":''},
      Stop2: {},
      endpoint: "http://localhost:8000"
    };
  }
  componentDidMount() {
    const { endpoint } = this.state;
    const socket = socketIOClient(endpoint);
    socket.emit("Stop1", data => console.log(data.Stop1));
    socket.on("Stop1", data => this.setState({ Stop1: JSON.parse(data) }));

  }
  render() {
    const { Stop1 } = this.state;
    console.log(Stop1["Route 1"][0]);

    return (

      <Container>

        {Stop1 ?

          <Row>


          <Col xs={12} md={12}>

              <Tabs defaultActiveKey="Stop1" id="uncontrolled-tab-example">
                <Tab eventKey="Stop1" title="Home">
                  <CardDeck>
                  {Object.keys(Stop1).map((keyName, i) => (
                    <Card bg="info" text="white">

                      <Card.Body>
                        <Card.Title>Route 2</Card.Title>
                        <Card.Text>
                          <ListGroup>
                            <ListGroup.Item>Cras justo odio</ListGroup.Item>
                            <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                          </ListGroup>
                        </Card.Text>
                      </Card.Body>

                    </Card>
                    ))}

                  </CardDeck>
                </Tab>
                <Tab eventKey="Stop2" title="Profile">
                  <CardDeck>
                    <Card bg="info" text="white">

                      <Card.Body>
                        <Card.Title>Route 1</Card.Title>
                        <Card.Text>
                          <ListGroup>
                            <ListGroup.Item>Cras justo odio</ListGroup.Item>
                            <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                          </ListGroup>
                        </Card.Text>
                      </Card.Body>

                    </Card>
                    <Card bg="info" text="white">

                      <Card.Body>
                        <Card.Title>Route 2</Card.Title>
                        <Card.Text>
                          <ListGroup>
                            <ListGroup.Item>Cras justo odio</ListGroup.Item>
                            <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                          </ListGroup>
                        </Card.Text>
                      </Card.Body>

                    </Card>
                    <Card bg="info" text="white">

                      <Card.Body>
                        <Card.Title>Route 3</Card.Title>
                        <Card.Text>
                          <ListGroup>
                            <ListGroup.Item>Cras justo odio</ListGroup.Item>
                            <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                          </ListGroup>
                        </Card.Text>
                      </Card.Body>

                    </Card>
                  </CardDeck>
                </Tab>

              </Tabs>

            </Col>
          </Row> : <div>Loading...</div>}
      </Container>

      /* <div style={{ textAlign: "center" }}>
         {response
             ? <p>
               time now: {response} 
               test :{}
             </p>
             : <p>Loading...</p>}
       </div>*/


    );
  }
}
export default App;
