import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import { Container, Row, Col, Tabs, Tab, CardDeck, Card, ListGroup } from 'react-bootstrap';

const CardArrival = (props) => {
  return (
    <Card bg="info" text="white">
    {Object.keys(props.data).map((keyName, i) => (


    <Card.Body>
      <Card.Title>Route 2</Card.Title>
      <Card.Text>
        <ListGroup>
          <ListGroup.Item >{props.data[keyName][0]}</ListGroup.Item>
          <ListGroup.Item>{props.data[keyName][1]}</ListGroup.Item>
        </ListGroup>
      </Card.Text>
    </Card.Body>


))}
  </Card>

  )
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      response: false,
      Stop1:{"R1":'',"R2":'',"R3":''},
      Stop2: {},
      endpoint: "http://localhost:8000"
    };
  }
  componentDidMount() {
    const { endpoint } = this.state;
    const socket = socketIOClient(endpoint);
    socket.emit("Stop1", data => console.log(data.Stop1));
    socket.on("Stop1", data => this.setState({ Stop1: JSON.parse(data) }));

  }
  render() {
    const { Stop1 } = this.state;


    return (

      <Container>

        {Stop1 ?

          <Row>


          <Col xs={12} md={12}>

              <Tabs defaultActiveKey="Stop1" id="uncontrolled-tab-example">
                <Tab eventKey="Stop1" title="Home">
                  <CardDeck>
                  <CardArrival data={Stop1}/>

                  </CardDeck>
                </Tab>
                <Tab eventKey="Stop2" title="Profile">
                  <CardDeck>

                  </CardDeck>
                </Tab>

              </Tabs>

            </Col>
          </Row> : <div>Loading...</div>}
      </Container>

      /* <div style={{ textAlign: "center" }}>
         {response
             ? <p>
               time now: {response} 
               test :{}
             </p>
             : <p>Loading...</p>}
       </div>*/


    );
  }
}
export default App;
