import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import { Container, Row, Col, Tabs, Tab, CardDeck, Card, ListGroup } from 'react-bootstrap';

const CardArrival = (props) => {
  return (
    <Card bg="info" text="black" >
    {Object.keys(props.data).map((keyName, i) => (


    <Card.Body>
      <Card.Title>Route {i+1}</Card.Title>
      <Card.Text>
        <ListGroup>
          <ListGroup.Item >{props.data[keyName][0]} minutes</ListGroup.Item>
          <ListGroup.Item>{props.data[keyName][1]} minutes</ListGroup.Item>
        </ListGroup>
      </Card.Text>
    </Card.Body>

))}
  </Card>

  )
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      response: false,
      Stop1:{"R1":'',"R2":'',"R3":''},
      Stop2:{"R1":'',"R2":'',"R3":''},
      endpoint: "http://localhost:8000"
    };
  // this.setKey = this.handleClick.bind(setKey);
  }
  componentDidMount() {

    this.connectSocket(1);
  }

  handleSelect(key) {
    console.log(key);
    if(key === 1)  
      this.connectSocket(1);
    else
    this.connectSocket(2);
   }

  connectSocket(stopId)
  { 
    let stop= "Stop"+stopId;
    const { endpoint } = this.state;

    const socket = socketIOClient(endpoint);
    console.log(stop)
    socket.emit(stop,"");
    socket.on(stop, data => this.setState({ [stop]: JSON.parse(data) }));
  }
  render() {
    const { Stop1,Stop2 } = this.state;


    return (

      <Container>

        {Stop1 ?

          <Row>


          <Col xs={12} md={12}>

              <Tabs defaultActiveKey="1" onSelect={k=>this.handleSelect(k)} id="uncontrolled-tab-example">
                <Tab eventKey="1" title="Stop 1">
                  <CardDeck>
                  <CardArrival data={Stop1}/>

                  </CardDeck>
                </Tab>
                <Tab eventKey="2" title="Stop 2">
                  <CardDeck>
                  <CardArrival data={Stop2}/>
                  </CardDeck>
                </Tab>

              </Tabs>

            </Col>
          </Row> : <div>Loading...</div>}
      </Container>


    );
  }
}
export default App;
